function isLogin() {
  return (
    <Row className="justify-content-between">
      <Popovers />
    </Row>
  );
}

class Header extends Component {
  render() {
    return (
      <div style={{ backgroundColor: "#FF5555" }}>
        <Container>
          <Row className="justify-content-between">
            <Col sm={2}>
              <Link to="/">
                <img src={Logo} alt="placeholder" />
              </Link>
            </Col>
            <Col sm={2} style={{ alignSelf: "center" }}>
              <isLogin />
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default Header;
