import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import {
    Button,
    Row,
    Container,
    Col,
} from "reactstrap";
import "../App.css";
import { } from "react-redux"
import FavoriteMap from "../mapping/favoriteMap";
import { TextField } from "@material-ui/core";
import { connect } from "react-redux"

const userId = localStorage.getItem("id")
const login = localStorage.getItem("isLogin")
const token = localStorage.getItem('token')

class EditProfile extends Component {

    render() {
        const { data } = this.props.user
        return (
            <div className="App">
                <Container>
                    <br></br>
                    <br></br>
                    <br></br>
                    <br></br>
                    <h1
                        style={{ textAlign: "left", color: "#FF5555", fontWeight: "700" }}
                    >
                        Profile
          </h1>
                    <Row>
                        <Col sm={6} className="profile">
                            <TextField label={`${data.name}`} />
                            <TextField label={`${data.DoB}`} />
                            <TextField label={`${data.phoneNumber}`} />
                            <TextField label={`${data.email}`} />
                        </Col>
                        <Col sm={2}>
                            <Button size="sm">Save</Button>
                        </Col>
                        <Col sm={4}>
                            <img
                                src={`https://sigmaestimates.com/wp-content/uploads/sites/2/2018/10/person-placeholder-1.png`}
                                width="100%"
                                height="350vh"
                                style={{ borderRadius: '50%' }}
                            />
                        </Col>
                    </Row>
                    <br></br>
                    <br></br>
                    <h1 style={{ textAlign: "left", color: "#FF5555", fontWeight: "700" }}>Favorites</h1>
                    <br></br>
                    <br></br>
                    <FavoriteMap />
                </Container>
            </div>
        )
    }
}


const mapStateToProps = state => {
    return {
        user: state.user
    };
};
const mapDispatchToProps = dispatch => {
    // return {
    //     addEvent: event => dispatch(addEvent(event))
    // };
};
export default connect(mapStateToProps, mapDispatchToProps)(EditProfile);
