import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import moment from "moment"
import { connect } from "react-redux"
import { editOrder } from "../_actions/order"

import "../App.css";
import QR from "../img/opera_JN4LpDssIv.png";
import Prove from "../img/opera_2bMnptgwbr.png";
import { getToken } from "../_config/bearer";


const login = localStorage.getItem("isLogin")
const token = localStorage.getItem('token')

class Payment extends Component {
    constructor(props) {
        super(props);
        this.state = {
            text: 'Pending',
            classNm: 'pen-Button',
            status: this.props.order.data.status,
            id: this.props.id
        };
    }


    handleStatus = () => {
        const orderID = { id: this.state.id }
        const order = { status: this.state.status }
        console.log(orderID)
        this.setState({ status: "confirmed" })
        this.setState({ text: 'confirmed' })
        this.setState({ classNm: 'payButton' })
        this.props.editOrder(order, orderID)
        window.location = "/payment"

    }

    render() {
        const startTime = new Date(this.props.date);
        const start = moment(startTime).format("ddd. DD MMM YYYY HH:mm")
        const { data } = this.props.order
        console.log(data)
        getToken(token)

        if (login == "true") {
            return (
                <div className="paymentBox" id={`${this.props.id}`}>
                    <br></br>
                    <br></br>
                    <br></br>
                    <div className="ticket">
                        <div className="ticket-header justify-content-between">
                            <div>
                                <h1>{this.props.name}</h1>
                                <p>Order ID : {this.props.id}</p>
                            </div>
                            <p>Face Value Rp{this.props.price}</p>
                        </div>
                        <div className="ticket-body justify-content-between">
                            <div style={{ width: "70%" }}>
                                <h1>{this.props.title}</h1>
                                <h2>{start}</h2>
                                <p>{this.props.address}</p>
                            </div>
                            <div style={{ width: "fitContent", paddingTop: "8px" }}>
                                <img src={QR} />
                            </div>
                        </div>
                    </div>
                    <div className="payment">
                        <br></br>
                        <h2>Shopping Summary</h2>
                        <div
                            className="justify-content-between"
                            style={{ display: "flex" }}
                        >
                            {" "}
                            <p>Total Price ({this.props.item} item)</p>
                            <p>Rp. {this.props.total}</p>
                        </div>
                    </div>
                    <hr></hr>
                    <div className="payment">
                        <h2>Prove of Payment</h2>
                        <div
                            className="justify-content-between"
                            style={{ display: "flex" }}
                        >
                            <img
                                src={Prove}
                                style={{ marginLeft: "20px", marginTop: "20px" }}
                            />
                            <a onClick={this.handleStatus} ><div className={`${this.state.classNm}`}>{this.state.text}</div></a>
                        </div>
                    </div>

                    <br></br>
                    <br></br>
                </div>

            );
        }

    }
}

const mapStateToProps = state => {
    return {
        order: state.order
    };
};
const mapDispatchToProps = dispatch => {
    return {
        editOrder: (order, id) => dispatch(editOrder(order, id))
    };
};
export default connect(mapStateToProps, mapDispatchToProps)(Payment);
