import { GET_EVENT_BY_CATEGORY } from "../_config/const";
import axios from "axios";

export const getEventByCategory = (id) => {
  return {
    type: GET_EVENT_BY_CATEGORY,
    payload: axios.get(
      "http://localhost:5000/api/v1/category/" + id + "/events"
    )
  };
}
