import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Button,
  Row,
  Container,
  FormGroup,
  Input,
  Form,
  Col
} from "reactstrap";
import { Typography, Grid, IconButton } from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";
import RemoveIcon from "@material-ui/icons/Remove";
import "../App.css";

let i = 0;
function buttonInc() {
  i++;
  document.getElementById("num").value = i;
}

function buttonDec() {
  i--;
  document.getElementById("num").value = i;
}

export default function Detail() {
  return (
    <Container>
      <br></br>
      <br></br>
      <div className="box">
        <img
          src="http://storage.jurnalotaku.com/wp-content/uploads/2017/07/c3-afaid-2017-fhana-fi-700x394.jpg"
          width="100%"
          style={{ marginBottom: "40px" }}
        />
        <div className="text-box">
          <Row className="justify-content-between">
            <h1>fhana Live in Jakarta</h1>
            <h2>300000</h2>
          </Row>
          <Row className="justify-content-between">
            <Col style={{ maxWidth: "fitContent" }}>
              <h4>Music</h4>
            </Col>
            <Col>
              <Row>
                <IconButton aria-label="min" onClick={buttonDec}>
                  <RemoveIcon />
                </IconButton>
                <input
                  id="num"
                  type="text"
                  value="0"
                  style={{
                    maxWidth: "20px",
                    textAlign: "center",
                    border: "none"
                  }}
                />
                <IconButton aria-label="add" onClick={buttonInc}>
                  <AddIcon />
                </IconButton>
                <Button>Buy</Button>{" "}
              </Row>
            </Col>
          </Row>
          <hr></hr>
          <Row className="justify-content-between">
            <Col>
              <h3>Hosted by</h3>
              <p>Juni Concert</p>
            </Col>
            <Col>
              <h3>Date & Time</h3>
              <p>13 Januari 2020 - 13 Januari 2020</p>
              <p>18:00 - 22:00</p>
            </Col>
            <Col style={{ padding: "0 30px" }}>
              <h3>Contact Person</h3>
              <p>Aoyama Sachiko</p>
              <p>+6200099998777</p>
              <p>email@gmail.com</p>
            </Col>
          </Row>
        </div>
      </div>
      <br></br>
      <br></br>
    </Container>
  );
}
