import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Container,
} from "reactstrap";
import {
  TextField,
  FormControl,
  Select,
  MenuItem,
  InputLabel
} from "@material-ui/core";
import {connect} from "react-redux"

import {addEvent} from "../_actions/event"
import "../App.css";
import Header from "../header/header";
import Footer from "../footer";


const userId = localStorage.getItem("id")

class AddEvent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title:'',
      category:'',
      img:'',
      price:'',
      description:'',
      address:'',
      email:'',
      telp:'',
      urlMaps:''
    };
  }

  
  handleAddEvent = () => {
    const event = {
      title: this.state.title,
      category:this.state.category,
      img:this.state.img,
      price:this.state.price,
      address:this.state.address,
      urlMaps:this.state.urlMaps,
      createdBy:userId
    };
    this.props.addEvent(event);
  };

  render() {
    return (
      <div className="App">
        <Header />
        <Container>
          <br></br>
          <br></br>
          <br></br>
          <br></br>
          <h1
            style={{ textAlign: "left", color: "#FF5555", fontWeight: "700" }}
          >
            Add Event
          </h1>
          <br></br>
          <br></br>
          <div className="form">
            <form>
              <TextField label="Title event" />
              <FormControl>
                <InputLabel id="demo-simple-select-label">Category</InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                >
                  <MenuItem value={1}>Sport</MenuItem>
                  <MenuItem value={2}>Music</MenuItem>
                  <MenuItem value={3}>Science</MenuItem>
                  <MenuItem value={4}>Programing</MenuItem>
                </Select>
              </FormControl>
              <TextField label="Upload Image" />{" "}
              <TextField
                id="datetime-local"
                label="Start Time"
                type="datetime-local"
                defaultValue="2017-05-24T10:30"
                InputLabelProps={{
                  shrink: true
                }}
              />
              <TextField
                id="datetime-local"
                label="End Time"
                type="datetime-local"
                defaultValue="2017-05-24T10:30"
                InputLabelProps={{
                  shrink: true
                }}
              />
              <TextField label="Price" />
              <TextField label="Address Event" />
              <TextField label="URL Map" />
              <TextField label="Telp No" />
              <TextField label="EO Email" />
              <TextField label="Event Description" />
            </form>
          </div>
          <br></br>
          <br></br>
          <div className="submit-button">Publish</div>
          <button>ini tombol</button>
          <br></br>
          <br></br>
          <br></br>
          <br></br>
        </Container>
        <Footer />
      </div>
    );
  }
}


const mapStateToProps = state => {
  return {
    event: state.event
  };
};
const mapDispatchToProps = dispatch => {
  return {
    addEvent: event => dispatch(addEvent(event))
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(AddEvent);
