import React, { Component } from "react";
import {
    FormGroup,
    Input,
    Form,
} from "reactstrap";
import { connect } from 'react-redux'

import { getEvents } from '../_actions/event'
import EventList from "../list/eventlist";

class Search extends Component {

    constructor(props) {
        super(props);
        this.state = {
            search: "",
            filter: false
        }
    }

    componentDidMount() {
        this.props.dispatch(getEvents())
    }



    render() {
        const { data } = this.props.event
        console.log(data)
        let filter;
        if (this.state.search == '') {
           filter = <div></div>
        } else {
            filter = data
                .filter(event => (event.title, result => result.includes(this.state.search)))
                .map(event => (
                    <EventList
                        key={event.id}
                        img={event.img}
                        title={event.title}
                        price={event.price}
                        brief={event.briefDesc}
                        time={event.startTime}
                    />
                ))

        }

        return (
            <Form inline>
                <FormGroup style={{ width: "95%" }}>
                    <Input
                        name="search"
                        id="exampleEmail"
                        placeholder="Cari..."
                        style={{ width: "100%" }}
                        value={this.state.search}
                        onChange={e => {
                            this.setState({
                                search: e.target.value
                            });
                        }}
                    />
                </FormGroup>
            </Form>

        );
    }
}

const mapStateToProps = state => {
    return {
        event: state.event
    };
};

export default connect(mapStateToProps)(Search);
