import React, { Component } from "react";
import {
  Button,
  Container,
  FormGroup,
  Input,
  Form,
} from "reactstrap";
import { connect } from 'react-redux'

import { getEvents } from './_actions/event'
import EventList from "./list/eventlist";
import CatButton from "./list/homelist";
import EventNow from "./mapping/eventnow"
import UpcomingEvent from "./mapping/upcomingEvent";
import Header from "./header/header";
import Footer from "./footer";

class Home extends Component {

  constructor(props) {
    super(props);
    this.state = {
      search: "",
      filter: false
    }
  }

  componentDidMount() {
    this.props.dispatch(getEvents())
  }



  render() {
    const { data } = this.props.event
    let filter;
    if (this.state.filter === true) {
      filter = data
        .filter(event => (event.title.include(this.state.search)))
        .map(event => (
          <EventList
            key={event.id}
            img={event.img}
            title={event.title}
            price={event.price}
            brief={event.briefDesc}
            time={event.startTime}
          />
        ))
    }

    return (
      <div className="App">
        <Header />
        <Container>
          <br></br>
          <br></br>
          <Form inline>
            <FormGroup style={{ width: "95%" }}>
              <Input
                name="search"
                id="exampleEmail"
                placeholder="Cari..."
                style={{ width: "100%" }}
                value={this.state.search}
                onChange={e => {
                  this.setState({
                    search: e.target.value,
                    filter: true
                  });
                }}
              />
            </FormGroup>
            <Button>Cari</Button>
          </Form>
          {filter}
          <br></br>
          <br></br>
          <h1 style={{ textAlign: "left" }}>Category</h1>
          <br></br>
          <CatButton />
          <br></br>
          <br></br>
          <br></br>
          <h1>Today</h1>
          <EventNow />
          <h1>Upcoming Event</h1>
          <UpcomingEvent />
        </Container>
        <Footer />
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    event: state.event
  };
};

export default connect(mapStateToProps)(Home);
