import React from "react";
import { Avatar, Link } from "@material-ui/core";
import { Popover, OverlayTrigger, Dropdown } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "../App.css";

function logout() {
  localStorage.removeItem("token");
  localStorage.removeItem("name");
  localStorage.removeItem("isLogin");
}

const popover = (
  <Popover id="popover-basic">
    <Popover.Content>
      <Dropdown.Item href="/newStory">Profile</Dropdown.Item>
      <Dropdown.Item href="/story">My Ticket</Dropdown.Item>
      <Dropdown.Item href="/stats">Payment</Dropdown.Item>
      <Dropdown.Item href="/stats">Add Event</Dropdown.Item>
      <Dropdown.Divider />
      <Dropdown.Item onClick={logout}>Logout</Dropdown.Item>
    </Popover.Content>
  </Popover>
);

function Popovers() {
  return (
    <OverlayTrigger trigger="click" placement="bottom" overlay={popover}>
      <Avatar style={{ fontSize: "15px", height: "50px", width: "50px" }}>
        R
      </Avatar>
    </OverlayTrigger>
  );
}

export default Popovers;
