import React, { Component } from "react";
import { connect } from "react-redux"
import { getCategory, getEventByCategory } from "../_actions/category"
import { TextField, Grid } from "@material-ui/core"
import { Container } from 'react-bootstrap'

import Header from "../header/header";
import Footer from "../footer";
import EventList from "../list/eventlist";

class Category extends Component {
  query = new URLSearchParams(window.location.search);
  url = this.query.get("id");

  componentDidMount() {
    const id = this.url;
    this.props.dispatch(getCategory(id));
    this.props.dispatch(getEventByCategory(id));
  }

  CategoryMap = () => {
    const { data } = this.props.categories;
    console.log(data);
    return (
      <Grid
        container
        direction="row"
        justify="space-between"
        alignItems="center"
        spacing={2}
        style={{ marginTop: "20px", marginBottom: "20px" }}
      >
        {data.map(event => (
          <EventList
            key={event.id}
            img={event.img}
            title={event.title}
            price={event.price}
            brief={event.briefDesc}
            time={event.startTime}
          />
        ))}
      </Grid>
    );
  }

  render() {
    const { data } = this.props.category
    return (
      <div className="App">
        <Header />
        <Container>
          <br></br>
          <br></br>
          <h1>{data.name}</h1>
          <br></br>
          <br></br>
          <h2>Sort by:</h2>
          <TextField
            type="date"
            defaultValue={'2020-01-01T10:30'}
            InputLabelProps={{
              shrink: true,
            }}
          // value={this.state.id} 
          // onChange={e => {
          //   this.setState({
          //     DoB: e.target.value
          //   });
          // }}
          />

          {this.CategoryMap}
        </Container>
        <Footer />
      </div>


    );
  }
}

class CatMap extends Component {


}



const mapStateToProps = state => {
  return {
    category: state.category,
    categories: state.eventByCat
  };
};

export default connect(mapStateToProps)(Category);
